mapper.select:
SELECT * FROM categories c WHERE c.is_deleted = 0
AND c.name LIKE #{name}
LIMIT #{recordStart},#{pageSize}

mapper.update:
UPDATE categories c
SET c.name = #{name}, c.update_user = #{updateUser}, c.update_time =
#{updateTime}
WHERE c.id = #{id};

UPDATE categories c
SET c.is_deleted = 1
WHERE c.id = #{id};
